function mintForPublic(uint256 amount) public payable {
    // Ensure the sale phase is active for public minting
    if (salePhase != 2) {
        revert SaleNotActive();
    }

    // Ensure the amount does not exceed the maximum allowed per transaction
    if (amount > 3) {
        revert ExceedsMaxPerTransaction();
    }

    // Ensure the sender's total minted tokens do not exceed the maximum allowed per address
    if (_mintedPerAddress[msg.sender] + amount > 3) {
        revert ExceedsMaxPerAddress();
    }

    // Ensure the total supply does not exceed the maximum collection size
    if (totalSupply() + amount > collectionSize) {
        revert ExceedsMaxSupply();
    }

    // Ensure the payment is sufficient for the requested amount
    if (msg.value < amount * MINT_PRICE) {
        revert InsufficientPayment();
    }

    // Mint the tokens to the sender
    _mint(msg.sender, amount);

    // Update the sender's minted token count
    _mintedPerAddress[msg.sender] += amount;
}

