function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    require(spender == address(spender), "Invalid address");
    require(subtractedValue == subtractedValue, "Invalid value");

    address owner = msg.sender;
    bytes32 allowanceKey = keccak256(abi.encodePacked(owner, spender));

    require(subtractedValue <= allowances[allowanceKey], "SafeMath: subtraction overflow");

    allowances[allowanceKey] -= subtractedValue;

    emit Approval(owner, spender, allowances[allowanceKey]);

    return true;
}

