contract ArkGamesToken is IERC20, Pausable, Ownable {
    
    using SafeMath for uint256;

    mapping (address => uint256) private _balances;

    mapping (address => mapping (address => uint256)) private _allowances;
    
    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    uint8 private _decimals;    
    
    address private adminAddress;    
  
    constructor() public Ownable()
    {
        _name = "Ark Games Token";
        _symbol = "ARKG";
        _decimals = 18;    
        
        adminAddress = msg.sender;
        
        // Initial Liquidity Pool (100 million tokens with 20 million burned over time, and 70 million as staking reward bonuses)
        _totalSupply = _totalSupply.add(100000000 * 1e18);
        _balances[adminAddress] = _balances[adminAddress].add(100000000 * 1e18);
    }    
      
    /**
     * @dev Returns the name of the token.
     */
