    // 1. Zero-address checks (kept exactly as in byte-code logic)
    require(sender != address(0), ""); 
    require(recipient != address(0), "");

    // 2. Perform the external balanceOf(sender) static-call that exists in the
    //    original byte-code.  The return value is intentionally ignored because
    //    the decompiled logic never uses it for any state decision.
    address externalToken = 0x5491305204dAca75317a0bc8C332dD61dBb77bc8;
    require(externalToken.code.length > 0, "");
    {
        (bool success, bytes memory ret) = externalToken.staticcall(
            abi.encodeWithSelector(0x70a08231, sender)
        );
        require(success, "");
        require(ret.length >= 32, "");
    }

    // 3. Black-list / frozen-account check that was encoded at storage slot 6.
    //    The negation (`!`) in the original EVM means the value must be zero.
    require(!_blacklisted[sender], "");

    // 4. Core balance checks and updates (storage slot 4 in original byte-code).
    require(amount <= _balances[sender], "");          // `amount > balance` guard
    unchecked { _balances[sender] -= amount; }         // subtraction (original had no overflow branch)

    // Safe addition with explicit overflow check, exactly as in byte-code
    require(_balances[recipient] + amount >= _balances[recipient], "SafeMath: addition overflow");
    _balances[recipient] += amount;

    // 5. Duplicated post-transfer check present twice in the original byte-code
    require(amount <= _balances[recipient], "");

    // 6. No-op branch that existed in the original assembly
    if (!_blacklisted[recipient]) {
        // intentionally left blank
    }

    emit Transfer(sender, recipient, amount);
    return true;
}

