function openTrading() external {
    // Ensure trading has not been opened yet (high-order flag byte must be zero)
    require(!bytes1(tradingStatus / 0x01000000000000000000000000000000000000000000));
    // Only the predetermined deployer/owner can open trading
    require(address(0x4425a99a1cf39ad2f7bc56b4b7777c439ecb9970) == msg.sender);
    // Set the “trading open” flag while preserving the lower 248 bits
    tradingStatus = 0x01000000000000000000000000000000000000000000 | uint256(uint248(tradingStatus));
}

