function royaltyInfo(uint256 tokenId, uint256 salePrice) public view returns (address receiver, uint256 royaltyAmount) {
    // Load the packed royalty data (address | uint24 bps) from storage
    uint256 packedRoyalty = _tokenRoyaltyInfo[tokenId];

    // The lower 160 bits hold the receiver address
    receiver = address(uint160(packedRoyalty));
    require(receiver != address(0), "RoyaltyInfo: receiver is zero address");

    // The upper 24 bits hold the royalty basis points (bps)
    uint24 royaltyBps = uint24(packedRoyalty >> 160);

    // Calculate royalty amount: (salePrice * bps) / 10_000
    royaltyAmount = (salePrice * royaltyBps) / 10_000;
}

