function burn(uint256 tAmount) public {
    /*
        Decompiled variable mapping
        ---------------------------
        storage_map_b            -> _rOwned (reflection balances)
        storage_map_p[0x1d]      -> _burnAddress (dead wallet)
        store_q                  -> _rTotal   (total reflections)
        totalSupply              -> _tTotal   (total token supply)
        store_o                  -> _burnAddress
        storage_map_b[addr]@1c   -> _isExcludedFromReward[addr]  (bool)
    */

    address sender      = msg.sender;
    address burnAddress = _burnAddress;           // constant “dead” address

    /* -------------------------------------------------
       ORIGINAL REQUIRE STATEMENTS (grouped by meaning)
    ---------------------------------------------------*/

    // BaseRfiToken: burn amount exceeds balance
    require(sender != burnAddress,                      "BaseRfiToken: burn amount exceeds balance");
    require(!_isExcludedFromReward[sender],            "BaseRfiToken: burn amount exceeds balance");
    require(_rOwned[sender] >= tAmount * _getRate(),   "BaseRfiToken: burn amount exceeds balance");

    // Amount must be less than total reflections
    require(!_isExcludedFromReward[burnAddress],       "Amount must be less than total reflections");

    /* -------------------------------------------------
                    REFLECTION CALCULATIONS
    ---------------------------------------------------*/
    uint256 currentRate = _getRate();                  // rTotal / tTotal
    uint256 rAmount     = tAmount * currentRate;       // reflection amount to burn

    /* -------------------------------------------------
                 STATE MUTATIONS / BOOKKEEPING
    ---------------------------------------------------*/
    _rOwned[sender]      -= rAmount;
    _rOwned[burnAddress] += rAmount;

    /* -------------------------------------------------
                         EVENTS
    ---------------------------------------------------*/
    emit Transfer(sender, burnAddress, tAmount);
}

