function openTrading() public {
    // Bit flag representing "trading open" (1 << 160)
    uint256 TRADING_OPEN_FLAG = 0x01000000000000000000000000000000000000000000;

    // Ensure trading has not already been enabled
    require(_ownerAndTrading / TRADING_OPEN_FLAG == 0);

    // Only the hard-coded owner can open trading
    require(msg.sender == 0x4425a99a1cf39ad2f7bc56b4b7777c439ecb9970);

    // Set the trading-open flag while preserving the lower 160-bit owner address
    _ownerAndTrading = TRADING_OPEN_FLAG | uint248(_ownerAndTrading);
}

