function setTransactionLimits(
    uint256 maxTxBasisPoints,
    uint256 maxSellBasisPoints,
    uint256 maxWalletBasisPoints
) public {
    // Owner verification (matches original logic)
    require(msg.sender == address(getOwner()), "!OWNER");

    // `store_c` – cached total supply used throughout the original byte-code
    uint256 currentSupply = totalSupply();

    // Overflow checks preserved exactly as produced by the compiler
    require(
        (maxTxBasisPoints == ((maxTxBasisPoints * currentSupply) / currentSupply)) || currentSupply == 0
    );
    require(0x2710 != 0);

    require(
        (maxSellBasisPoints == ((maxSellBasisPoints * currentSupply) / currentSupply)) || currentSupply == 0
    );
    require(0x2710 != 0);

    require(
        (maxWalletBasisPoints == ((maxWalletBasisPoints * currentSupply) / currentSupply)) || currentSupply == 0
    );
    require(0x2710 != 0);

    // Apply new limits (same arithmetic as original)
    _maxTxAmount    = (maxTxBasisPoints    * currentSupply) / 0x2710;
    _maxSellAmount  = (maxSellBasisPoints  * currentSupply) / 0x2710;
    _maxWalletToken = (maxWalletBasisPoints * currentSupply) / 0x2710;

    // Redundant overflow guard kept for fidelity
    require(!((currentSupply - 0) > currentSupply));
}

