function openTrading() public {
    uint256 tradingOpenFlag = 0x01000000000000000000000000000000000000000000;

    // Ensure trading is not already open
    require(!bytes1(_statusFlags / tradingOpenFlag));

    // Only the designated owner can open trading
    require(msg.sender == 0x9Cb6dA40d96aA44Cc0314DF6814033C3513E75A6);

    // Mark trading as open by setting the corresponding flag
    _statusFlags = tradingOpenFlag | uint256(uint248(_statusFlags));
}

