function burn(uint256 amount) public {
    require(msg.sender != address(0), "BaseRfiToken: burn amount exceeds balance");
    require(msg.sender != burnAddress, "BaseRfiToken: burn amount exceeds balance");
    require(!excludedFromReward[msg.sender], "BaseRfiToken: burn amount exceeds balance");
    require(balanceOf[msg.sender] >= amount, "BaseRfiToken: burn amount exceeds balance");

    uint256 reflectionRate = totalReflections / totalSupply;
    uint256 reflectionAmount = amount * reflectionRate;

    require(reflectionAmount <= type(uint256).max / amount, "BaseRfiToken: burn amount exceeds balance");
    require(balanceOf[burnAddress] >= reflectionAmount, "BaseRfiToken: burn amount exceeds balance");

    balanceOf[msg.sender] -= reflectionAmount;
    balanceOf[burnAddress] += reflectionAmount;

    emit Transfer(msg.sender, burnAddress, amount);
}

