function balanceOf(address account) public view returns (uint256) {
    require(account == address(account), "Invalid address");

    uint256 reflectedBalance = _reflectedBalances[account];
    uint256 totalReflections = _totalReflections;

    require(
        reflectedBalance <= totalReflections,
        "Amount must be less than total reflections"
    );

    // Calculate the actual balance based on the current rate
    uint256 currentRate = _getRate();
    uint256 actualBalance = reflectedBalance / currentRate;

    return actualBalance;
}

