function mintForPublic(uint256 amount) public payable {
    // Ensure the sale phase is active for public minting
    require(salePhase == 2, SaleNotActive());

    // Check that the requested amount does not exceed the maximum allowed per transaction
    require(amount <= maxBatchSizeForWL, ExceedsMaxPerTransaction());

    // Check that the sender's total minted tokens (including this transaction) does not exceed the maximum allowed per address
    require(_mintedPerAddress[msg.sender] + amount <= maxPerAddressDuringMint, ExceedsMaxPerAddress());

    // Check that the total supply after minting does not exceed the maximum supply
    require(totalSupply() + amount <= collectionSize, ExceedsMaxSupply());

    // Ensure sufficient payment is provided for the minting
    require(msg.value >= amount * MINT_PRICE, InsufficientPayment());

    // Mint the tokens to the sender
    for (uint256 i = 0; i < amount; i++) {
        _safeMint(msg.sender, totalSupply() + i);
    }

    // Update the sender's minted token count
    _mintedPerAddress[msg.sender] += amount;
}

