function burn(uint256 amount) public {
    // Ensure the caller's address is valid (redundant check, but kept for fidelity)
    require(msg.sender != address(0), "ERC20: burn amount exceeds balance");

    // Ensure the caller has enough balance to burn
    require(balances[msg.sender] >= amount, "ERC20: burn amount exceeds balance");

    // Deduct the burn amount from the caller's balance
    balances[msg.sender] -= amount;

    // Reduce the total supply by the burn amount
    totalSupply -= amount;

    // Emit the Transfer event to indicate a burn (to the zero address)
    emit Transfer(msg.sender, address(0), amount);
}
