function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    require(spender == address(spender), "Invalid address");

    address owner = msg.sender;
    bytes32 allowanceKey = keccak256(abi.encodePacked(owner, spender));

    require(!(allowances[allowanceKey] > type(uint256).max - addedValue), "ERC20: allowance overflow");

    allowances[allowanceKey] += addedValue;

    emit Approval(owner, spender, allowances[allowanceKey]);

    return true;
}

