function royaltyInfo(uint256 tokenId, uint256 salePrice) public view returns (bytes memory) {
    // Retrieve the royalty recipient address from storage
    address royaltyRecipient = address(storageMap[tokenId]);
    require(royaltyRecipient != address(0), "Invalid royalty recipient");

    // Calculate the royalty percentage
    uint24 royaltyPercentage = uint24(storageMap[tokenId] >> 160);

    // Ensure the royalty calculation does not overflow
    require(
        salePrice == 0 || 
        royaltyPercentage <= (type(uint256).max / salePrice),
        "Royalty calculation overflow"
    );

    // Calculate the royalty amount
    uint256 royaltyAmount = (salePrice * royaltyPercentage) / 10000;

    // Return the encoded royalty recipient and amount
    return abi.encodePacked(royaltyRecipient, royaltyAmount);
}

