function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    address owner = msg.sender;
    bytes32 allowanceKey = keccak256(abi.encode(owner, spender));

    // Ensure the subtracted value does not exceed the current allowance
    require(subtractedValue <= allowances[allowanceKey], "ERC20: decreased allowance below zero");

    // Update the allowance
    allowances[allowanceKey] -= subtractedValue;

    // Emit the Approval event
    emit Approval(owner, spender, allowances[allowanceKey]);

    return true;
}

