function getBalanceERC20(uint256 tokenIndex) public view returns (uint256) {
    require(tokenIndex < allowedTokens.length, "Invalid token index");

    address tokenAddress = allowedTokens[tokenIndex].paytoken;
    address contractAddress = address(this);

    (bool success, bytes memory balanceData) = tokenAddress.staticcall(
        abi.encodeWithSelector(0x70a08231, contractAddress) // ERC20 balanceOf selector
    );

    require(success, "Failed to fetch token balance");
    require(balanceData.length >= 32, "Invalid balance data");

    uint256 balance = abi.decode(balanceData, (uint256));
    return balance;
}

