function manualBurn(uint256 amount) public returns (bool) {
    require(amount == amount, "Invalid amount"); // Placeholder check, likely redundant
    require(msg.sender == getOwner(), "!Owner");

    address contractAddress = address(this);

    // Ensure the contract has enough balance to burn
    require(amount <= balances[contractAddress], "Insufficient Balance");

    // Subtract the burn amount from the contract's balance
    balances[contractAddress] -= amount;

    // Add the burn amount to the burn address (0x000...dead)
    address burnAddress = 0x000000000000000000000000000000000000dEaD;
    require(
        balances[burnAddress] + amount >= balances[burnAddress],
        "SafeMath: addition overflow"
    );
    balances[burnAddress] += amount;

    // Emit the Transfer event to reflect the burn
    emit Transfer(contractAddress, burnAddress, amount);

    return true;
}

