function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    require(spender != address(0), "ERC20: approve to the zero address");
    require(addedValue != 0, "Amount must be more than 0");

    address owner = msg.sender;
    require(owner != address(0), "ERC20: approve from the zero address");

    uint256 currentAllowance = allowances[owner][spender];
    require(currentAllowance + addedValue >= currentAllowance, "ERC20: allowance addition overflow");

    allowances[owner][spender] = currentAllowance + addedValue;

    emit Approval(owner, spender, allowances[owner][spender]);
    return true;
}

