function removeERCToken(address tokenAddress, uint256 tokens) public returns (bool success) {
    require(tokenAddress != address(0), "Invalid token address");
    require(tokens > 0, "Invalid token amount");

    emit ClearToken(tokenAddress, tokens);

    // Prepare the transfer call
    bytes4 transferSelector = bytes4(keccak256("transfer(address,uint256)"));
    address recipient = msg.sender;

    // Call the token contract's transfer function
    (bool callSuccess, bytes memory returnData) = tokenAddress.call(
        abi.encodeWithSelector(transferSelector, recipient, tokens)
    );

    // Check if the call was successful
    require(callSuccess, "Token transfer failed");

    // Decode the return value (if any) to ensure it matches the expected success value
    if (returnData.length > 0) {
        require(abi.decode(returnData, (bool)), "Token transfer returned false");
    }

    return true;
}

