function updateMaxTxnAmount(uint256 maxTransactionPercent, uint256 maxWalletPercent) public {
    require(msg.sender == owner, "Ownable: caller is not the owner");

    // Ensure maxTransactionPercent is at least 1% of the total supply
    require(maxTransactionPercent >= 1, "Cannot set maxTransactionAmount lower than 1%");
    require(
        maxTransactionPercent == (maxTransactionPercent * totalSupply) / totalSupply || totalSupply == 0,
        "Cannot set maxTransactionAmount lower than 1%"
    );

    // Ensure maxWalletPercent is at least 1% of the total supply
    require(maxWalletPercent >= 1, "Cannot set maxWallet lower than 1%");
    require(
        maxWalletPercent == (maxWalletPercent * totalSupply) / totalSupply || totalSupply == 0,
        "Cannot set maxWallet lower than 1%"
    );

    // Update the max transaction and wallet percentages
    maxTransactionAmount = (totalSupply * maxTransactionPercent) / 100;
    maxWallet = (totalSupply * maxWalletPercent) / 100;
}

