function transfer(address recipient, uint256 amount) public returns (bool) {
    // Ensure recipient address is valid
    require(recipient == address(recipient), "Invalid recipient address");
    // Ensure amount is valid
    require(amount == amount, "Invalid transfer amount");
    // Check sender's balance
    require(balanceOf(msg.sender) >= amount, "ERC20: transfer amount exceeds balance");
    // Check recipient's balance (redundant, but kept for logical fidelity)
    require(balanceOf(recipient) >= 0, "ERC20: transfer amount exceeds balance");

    // Deduct amount from sender's balance
    balances[msg.sender] -= amount;
    // Add amount to recipient's balance
    balances[recipient] += amount;

    // Emit Transfer event
    emit Transfer(msg.sender, recipient, amount);

    // Return success
    return true;
}

