function balanceOf(address account) public view returns (uint256) {
    require(account == address(account), "Invalid address");

    // Check if the account is excluded from rewards
    if (excludedFromReward[account]) {
        return tokenBalances[account];
    }

    // Calculate the balance based on reflection
    uint256 currentRate = _getRate();
    return reflectionBalances[account] / currentRate;
}

// Helper function to calculate the current rate
function _getRate() private view returns (uint256) {
    uint256 totalReflectionSupply = _totalReflectionSupply;
    uint256 totalTokenSupply = _totalTokenSupply;

    // Ensure the rate calculation does not divide by zero
    if (totalReflectionSupply == 0 || totalTokenSupply == 0) {
        revert("Reflection supply or token supply is zero");
    }

    return totalReflectionSupply / totalTokenSupply;
}

