contract RefinedContract {
    address private _executor;

    event ExecutorUpdated(address indexed previousExecutor, address indexed newExecutor);

    function setExecutor(address newExecutor) public {
        require(newExecutor != address(0), "Invalid address");
        require(msg.sender == _executor, "Access denied");

        address previousExecutor = _executor;
        _executor = newExecutor;

        emit ExecutorUpdated(previousExecutor, newExecutor);
    }
}
