function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    // Ensure the spender address is valid
    require(spender != address(0), "Invalid spender address");

    // Retrieve the caller's address
    address owner = msg.sender;

    // Check for underflow in allowance subtraction
    require(allowances[owner][spender] >= subtractedValue, "SafeMath: subtraction overflow");

    // Update the allowance
    allowances[owner][spender] -= subtractedValue;

    // Emit the Approval event
    emit Approval(owner, spender, allowances[owner][spender]);

    // Return true to indicate success
    return true;
}

