function pendingRewards(address userAddress) public view returns (uint256 reward) {
    require(userAddress == address(userAddress), "Invalid address");

    uint256 depositAmount = depositeToken[userAddress][0];
    uint256 lockableDays = lockabledays[userAddress][0];
    uint256 depositTime = depositetime[userAddress][0];

    require(depositAmount > 0, "No deposit found");
    require(lockableDays > 0, "Invalid lockable days");

    uint256 lockTime = depositTime + (lockableDays * time);
    require(block.timestamp > lockTime, "Lock period not yet completed");

    uint256 allocationPercentage = allocation[lockableDays];
    require(allocationPercentage > 0, "Invalid allocation percentage");

    reward = (allocationPercentage * depositAmount) / 100;
    reward = reward / 1e18;

    return reward;
}

