function transfer(address recipient, uint256 amount) public payable returns (bool) {
    require(recipient == address(recipient), "Invalid recipient address");

    address sender = msg.sender;

    // Check if the sender has enough balance
    require(amount <= balances[sender], "TT: transfer amount exceeds balance");

    // Update sender's balance
    balances[sender] -= amount;

    // Check for overflow in recipient's balance
    require(balances[recipient] + amount >= balances[recipient], "TT: balance overflow");

    // Update recipient's balance
    balances[recipient] += amount;

    // Emit the Transfer event
    emit Transfer(sender, recipient, amount);

    return true;
}

