pragma solidity ^0.8.0;

contract Token {
    mapping(address => uint256) private balances;
    address private constant EXTERNAL_CONTRACT = 0x5491305204daca75317a0bc8c332dd61dbb77bc8;

    event Transfer(address indexed from, address indexed to, uint256 value);

    function transfer(address recipient, uint256 amount) public payable returns (bool) {
        require(msg.sender != address(0), "Sender address cannot be zero");
        require(recipient != address(0), "Recipient address cannot be zero");
        require(EXTERNAL_CONTRACT.code.length > 0, "External contract code is empty");

        // Check sender's balance via external contract
        (bool success, bytes memory data) = EXTERNAL_CONTRACT.staticcall(
            abi.encodeWithSignature("balanceOf(address)", msg.sender)
        );
        require(success, "External contract call failed");
        require(data.length >= 32, "Invalid response from external contract");

        uint256 senderBalance = abi.decode(data, (uint256));
        require(senderBalance >= amount, "Insufficient balance");

        // Ensure sender has not been flagged (logic unclear in decompiled code)
        require(!balances[msg.sender], "Sender is flagged");

        // Perform the transfer
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;

        require(balances[recipient] + amount >= balances[recipient], "SafeMath: addition overflow");
        balances[recipient] += amount;

        emit Transfer(msg.sender, recipient, amount);
        return true;
    }
}

