function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    // Ensure the spender address is valid (decompiler artifact, redundant check)
    require(spender == address(spender), "Invalid spender address");
    require(addedValue == addedValue, "Invalid added value"); // Redundant check

    // Calculate the storage key for the spender's allowance
    address owner = msg.sender;
    bytes32 allowanceKey = keccak256(abi.encode(owner, spender));

    // Check for overflow in the allowance addition
    require(
        !(allowances[owner][spender] > allowances[owner][spender] + addedValue),
        "ERC20: allowance overflow"
    );

    // Update the allowance
    allowances[owner][spender] += addedValue;

    // Emit the Approval event
    emit Approval(owner, spender, allowances[owner][spender]);

    // Return true to indicate success
    return true;
}

