pragma solidity ^0.8.0;

contract ERC20Token {
    mapping(address => uint256) private balances;
    event Transfer(address indexed from, address indexed to, uint256 value);

    function transfer(address recipient, uint256 amount) public payable returns (bool) {
        require(recipient == address(recipient), "Invalid recipient address");
        require(amount == amount, "Invalid transfer amount"); // Redundant check, but kept for fidelity
        require(balances[msg.sender] >= amount, "ERC20: transfer amount exceeds balance");
        require(balances[recipient] + amount >= balances[recipient], "ERC20: transfer amount overflows");

        // Deduct from sender's balance
        balances[msg.sender] -= amount;

        // Add to recipient's balance
        balances[recipient] += amount;

        // Emit transfer event
        emit Transfer(msg.sender, recipient, amount);

        return true;
    }
}

