    require(spender != address(0), "ERC20: approve to the zero address");

    // Calculate the storage key for the allowance mapping
    bytes32 allowanceKey = keccak256(abi.encodePacked(msg.sender, spender));

    // Check for overflow in the addition
    require(allowances[msg.sender][spender] + addedValue >= allowances[msg.sender][spender], "ERC20: addition overflow");

    // Update the allowance
    allowances[msg.sender][spender] += addedValue;

