function transfer(address recipient, uint256 amount) public payable returns (bool) {
    require(recipient == address(recipient), "Invalid recipient address");
    require(msg.sender != address(0), "ERC20: transfer from the zero address");
    require(recipient != address(0), "ERC20: transfer to the zero address");

    address sender = msg.sender;

    // Check if sender has enough balance
    require(balanceOf[sender] >= amount, "ERC20: transfer amount exceeds balance");

    // Deduct the amount from sender's balance
    balanceOf[sender] -= amount;

    // Add the amount to recipient's balance
    balanceOf[recipient] += amount;

    // Emit the Transfer event
    emit Transfer(sender, recipient, amount);

    return true;
}

