function setMaxSize(uint256 maxTransactionSize, uint256 maxTransferSize, uint256 maxWalletSize) public {
    require(msg.sender == getOwner(), "Ownable: Caller is not owner");

    // Validate maxTransactionSize
    require(
        maxTransactionSize == (maxTransactionSize * store_l) / store_l || store_l == 0,
        "Invalid maxTransactionSize"
    );
    require(0x2710 > 0, "Invalid constant check");

    // Validate maxTransferSize
    require(
        maxTransferSize == (maxTransferSize * store_l) / store_l || store_l == 0,
        "Invalid maxTransferSize"
    );
    require(0x2710 > 0, "Invalid constant check");

    // Validate maxWalletSize
    require(
        maxWalletSize == (maxWalletSize * store_l) / store_l || store_l == 0,
        "Invalid maxWalletSize"
    );
    require(0x2710 > 0, "Invalid constant check");

    // Set the maximum sizes
    _maxxTrxSize = (maxTransactionSize * store_l) / 0x2710;
    _maxxTransferSize = (maxTransferSize * store_l) / 0x2710;
    _maxxWalletSize = (maxWalletSize * store_l) / 0x2710;

    // Final validation
    require(!(store_l - store_n > store_l), "Invalid store_l and store_n relationship");
}

