// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ERC20 {
    // Mapping to store allowances
    mapping(address => mapping(address => uint256)) private _allowances;

    // Event to emit when an approval is updated
    event Approval(address indexed owner, address indexed spender, uint256 value);

    /**
     * @dev Decreases the allowance granted to `spender` by the caller.
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     * - `spender` cannot be the zero address.
     * - `subtractedValue` cannot exceed the current allowance.
     *
     * @param spender The address which will spend the funds.
     * @param subtractedValue The amount by which the allowance will be decreased.
     * @return success A boolean value indicating whether the operation succeeded.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool success) {
        address owner = msg.sender;

        require(spender != address(0), "ERC20: decreaseAllowance to the zero address");
        require(_allowances[owner][spender] >= subtractedValue, "ERC20: decreased allowance below zero");

        // Decrease the allowance
        _allowances[owner][spender] -= subtractedValue;

        // Emit the Approval event
        emit Approval(owner, spender, _allowances[owner][spender]);

        return true;
    }

    /**
     * @dev Returns the current allowance for a given owner and spender.
     * @param owner The address of the token owner.
     * @param spender The address of the spender.
     * @return remaining The remaining allowance.
     */
    function allowance(address owner, address spender) public view returns (uint256 remaining) {
        return _allowances[owner][spender];
    }

    /**
     * @dev Internal function to set an allowance.
     * @param owner The address of the token owner.
     * @param spender The address of the spender.
     * @param amount The amount of allowance to set.
     */
    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
}
