contract ExampleContract {
    address private owner;
    bool public tradingActive;

    event TradingLimitsRemoved();

    constructor() {
        owner = msg.sender;
        tradingActive = true; // Assuming trading is initially active
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    function removeLimits() public onlyOwner returns (bool) {
        tradingActive = false; // Disable trading limits
        emit TradingLimitsRemoved();
        return true;
    }
}
