// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    address public owner;
    bool public transferDelayEnabled;
    uint256 public maxTxAmount;
    uint256 public taxSwapThreshold;
    uint256 public maxTaxSwap;

    mapping(address => uint256) private lastTransferBlock;
    mapping(address => uint256) private balances;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier transferAllowed(address to, uint256 amount) {
        require(to != address(0), "Transfer to zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount <= maxTxAmount, "Exceeds the max transaction amount");
        require(block.number > lastTransferBlock[msg.sender], "Only one transfer per block allowed");
        _;
    }

    constructor() {
        owner = msg.sender;
        transferDelayEnabled = true;
        maxTxAmount = 1000 * 10**18; // Example value
        taxSwapThreshold = 100 * 10**18; // Example value
        maxTaxSwap = 500 * 10**18; // Example value
    }

    function transfer(address to, uint256 amount) public transferAllowed(to, amount) {
        uint256 taxAmount = calculateTax(amount);
        uint256 transferAmount = amount - taxAmount;

        require(balances[msg.sender] >= amount, "Insufficient balance");
        require(balances[msg.sender] - amount <= balances[msg.sender], "SafeMath: subtraction overflow");

        balances[msg.sender] -= amount;
        balances[to] += transferAmount;

        if (taxAmount > 0) {
            balances[address(this)] += taxAmount;
        }

        lastTransferBlock[msg.sender] = block.number;

        emit Transfer(msg.sender, to, transferAmount);
    }

    function calculateTax(uint256 amount) internal view returns (uint256) {
        if (amount > taxSwapThreshold) {
            uint256 tax = (amount * 5) / 100; // Example 5% tax
            return tax > maxTaxSwap ? maxTaxSwap : tax;
        }
        return 0;
    }

    function setMaxTxAmount(uint256 _maxTxAmount) external onlyOwner {
        maxTxAmount = _maxTxAmount;
    }

    function setTaxSwapThreshold(uint256 _taxSwapThreshold) external onlyOwner {
        taxSwapThreshold = _taxSwapThreshold;
    }

    function setMaxTaxSwap(uint256 _maxTaxSwap) external onlyOwner {
        maxTaxSwap = _maxTaxSwap;
    }

    function setTransferDelayEnabled(bool _enabled) external onlyOwner {
        transferDelayEnabled = _enabled;
    }

    function deposit() external payable {
        balances[msg.sender] += msg.value;
    }

    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
    }

    function balanceOf(address account) external view returns (uint256) {
        return balances[account];
    }
}
