// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Burnable {
    address public owner;
    mapping(address => uint256) public balances;

    event Burn(address indexed account, uint256 amount);
    event Mint(address indexed account, uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function burnTokens(address account, uint256 amount) public onlyOwner {
        require(account != address(0), "Invalid account address");
        require(amount > 0, "Amount must be greater than zero");
        require(balances[account] >= amount, "Insufficient balance to burn");

        // Burn tokens from the specified account
        balances[account] -= amount;

        emit Burn(account, amount);
    }

    function mintTokens(address account, uint256 amount) public onlyOwner {
        require(account != address(0), "Invalid account address");
        require(amount > 0, "Amount must be greater than zero");

        // Mint tokens to the specified account
        balances[account] += amount;

        emit Mint(account, amount);
    }
}
```

