// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract DividendClaimer {
    address public blacklistContract;

    constructor(address _blacklistContract) {
        require(_blacklistContract != address(0), "Invalid blacklist contract address");
        blacklistContract = _blacklistContract;
    }

    function claimDividend() public {
        require(isContract(blacklistContract), "Blacklist contract is not valid");

        (bool success, ) = blacklistContract.call(
            abi.encodeWithSignature("claim(address)", msg.sender)
        );
        require(success, "Dividend claim failed");
    }

    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }
}
```

