// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ERC20 {
    // Mapping to store allowances
    mapping(address => mapping(address => uint256)) private _allowances;

    // Event to emit when an approval is made
    event Approval(address indexed owner, address indexed spender, uint256 value);

    /**
     * @dev Increases the allowance granted to `spender` by the caller.
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        require(spender != address(0), "ERC20: approve to the zero address");

        address owner = msg.sender;

        // Update the allowance
        _allowances[owner][spender] += addedValue;

        // Emit the Approval event
        emit Approval(owner, spender, _allowances[owner][spender]);

        return true;
    }

    /**
     * @dev Returns the remaining number of tokens that `spender` will be allowed
     * to spend on behalf of `owner` through {transferFrom}. This is zero by default.
     */
    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }
}
