// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract BurnModeController {
    address public owner;
    uint256 public burnMode;

    // Modifier to restrict access to the owner
    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    // Constructor to set the initial owner
    constructor() {
        owner = msg.sender;
    }

    // Function to activate burn mode
    function activateBurnMode() public onlyOwner {
        // Set the burnMode flag by OR-ing with a specific bit pattern
        burnMode |= 0x01000000000000000000000000000000000000000000;
    }
}
```

