// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract LiquidityBurner {
    address public owner;
    uint256 public lastManualLpBurnTime;
    uint256 public manualBurnFrequency = 1 days; // Example cooldown period
    uint256 public constant MAX_BURN_PERCENTAGE = 10; // Maximum 10% of LP tokens can be burned
    address public liquidityPairAddress = 0x54a875b040e5413f27fb1ebe7af5180daf5e264a; // Example LP token address
    address public constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    event ManualNukeLP();
    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function manualBurnLiquidityPairTokens(uint256 percentage) external onlyOwner returns (bool) {
        require(block.timestamp > lastManualLpBurnTime + manualBurnFrequency, "Cooldown period not finished");
        require(percentage <= MAX_BURN_PERCENTAGE, "Cannot burn more than 10% of LP tokens");

        lastManualLpBurnTime = block.timestamp;

        uint256 lpTokenBalance = IERC20(liquidityPairAddress).balanceOf(address(this));
        uint256 burnAmount = (lpTokenBalance * percentage) / 100;

        require(burnAmount > 0, "Burn amount must be greater than zero");
        require(lpTokenBalance >= burnAmount, "Insufficient LP token balance");

        IERC20(liquidityPairAddress).transfer(DEAD_ADDRESS, burnAmount);

        emit Transfer(address(this), DEAD_ADDRESS, burnAmount);
        emit ManualNukeLP();

        return true;
    }
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}
