// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RefactoredContract {
    address public owner;
    uint256 public maxTxAmount;
    uint256 public taxSwapThreshold;
    uint256 public maxTaxSwap;
    uint256 public taxRate; // Represented as a percentage (e.g., 5 for 5%)
    mapping(address => uint256) public balances;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        maxTxAmount = 1000 * 10**18; // Example value
        taxSwapThreshold = 100 * 10**18; // Example value
        maxTaxSwap = 500 * 10**18; // Example value
        taxRate = 5; // 5% tax rate
    }

    function transfer(address recipient, uint256 amount) public {
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount <= maxTxAmount, "Transfer amount exceeds the maxTxAmount");
        require(balances[msg.sender] >= amount, "Insufficient balance");

        uint256 taxAmount = (amount * taxRate) / 100;
        uint256 transferAmount = amount - taxAmount;

        require(transferAmount + taxAmount == amount, "SafeMath: addition overflow");

        // Deduct the amount from sender
        balances[msg.sender] -= amount;

        // Add the transfer amount to the recipient
        balances[recipient] += transferAmount;

        // Handle tax
        if (taxAmount > 0) {
            balances[address(this)] += taxAmount;
            emit Transfer(msg.sender, address(this), taxAmount);
        }

        emit Transfer(msg.sender, recipient, transferAmount);
    }

    function withdrawTax() public onlyOwner {
        uint256 taxBalance = balances[address(this)];
        require(taxBalance > 0, "No tax to withdraw");
        require(taxBalance <= maxTaxSwap, "Tax amount exceeds maxTaxSwap");

        balances[address(this)] = 0;
        balances[owner] += taxBalance;

        emit Transfer(address(this), owner, taxBalance);
    }

    function setMaxTxAmount(uint256 _maxTxAmount) public onlyOwner {
        require(_maxTxAmount > 0, "MaxTxAmount must be greater than zero");
        maxTxAmount = _maxTxAmount;
    }

    function setTaxRate(uint256 _taxRate) public onlyOwner {
        require(_taxRate <= 100, "Tax rate must be between 0 and 100");
        taxRate = _taxRate;
    }

    function setTaxSwapThreshold(uint256 _taxSwapThreshold) public onlyOwner {
        require(_taxSwapThreshold > 0, "TaxSwapThreshold must be greater than zero");
        taxSwapThreshold = _taxSwapThreshold;
    }

    function setMaxTaxSwap(uint256 _maxTaxSwap) public onlyOwner {
        require(_maxTaxSwap > 0, "MaxTaxSwap must be greater than zero");
        maxTaxSwap = _maxTaxSwap;
    }

    function deposit() public payable {
        balances[msg.sender] += msg.value;
    }

    function getBalance(address account) public view returns (uint256) {
        return balances[account];
    }
}
