pragma solidity ^0.8.0;

contract Token {
    address public owner;
    address public uniswapV2Pair;
    uint256 public maxTransactionAmount;
    mapping(address => bool) public isBlacklisted;
    bool public tradingEnabled;

    modifier onlyOwner() {
        require(msg.sender == owner, "TOKEN: Caller is not the owner");
        _;
    }

    modifier tradingAllowed() {
        require(tradingEnabled, "TOKEN: Trading is not enabled");
        _;
    }

    modifier notBlacklisted(address account) {
        require(!isBlacklisted[account], "TOKEN: Account is blacklisted");
        _;
    }

    modifier validTransfer(address to, uint256 amount) {
        require(to != address(0), "TOKEN: Transfer to the zero address");
        require(amount > 0, "TOKEN: Transfer amount must be greater than zero");
        require(amount <= maxTransactionAmount, "TOKEN: Transfer amount exceeds the max limit");
        _;
    }

    constructor(uint256 _maxTransactionAmount) {
        owner = msg.sender;
        maxTransactionAmount = _maxTransactionAmount;
        tradingEnabled = false;
    }

    function enableTrading() external onlyOwner {
        tradingEnabled = true;
    }

    function blacklistAddress(address account, bool value) external onlyOwner {
        isBlacklisted[account] = value;
    }

    function setUniswapV2Pair(address pair) external onlyOwner {
        uniswapV2Pair = pair;
