// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ImprovedContract {

    mapping(address => uint256) public balances;
    uint256 public swapTokensAtAmount;
    uint256 public maxTransactionAmount;
    uint256 public sellTotalFees;
    uint256 public buyTotalFees;
    address public owner;
    bool public tradingActive;
    address public uniswapV2Pair;

    event Transfer(address indexed from, address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the contract owner");
        _;
    }

    modifier tradingEnabled() {
        require(tradingActive, "Trading is not active");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function transfer(address to, uint256 amount) public tradingEnabled {
        require(to != address(0), "Transfer to the zero address");
        require(amount <= balances[msg.sender], "Transfer amount exceeds balance");
        require(amount <= maxTransactionAmount, "Transfer amount exceeds the maxTransactionAmount");

        uint256 senderBalanceBefore = balances[msg.sender];
        uint256 receiverBalanceBefore = balances[to];

        balances[msg.sender] -= amount;
        balances[to] += amount;

        emit Transfer(msg.sender, to, amount);

        require(senderBalanceBefore == balances[msg.sender] + amount, "Balance mismatch");
        require(receiverBalanceBefore + amount == balances[to], "Balance mismatch");
    }
}
