// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract FeeManager {
    address public owner;
    uint256 public sellFee;

    event SellFeeUpdated(uint256 newSellFee);

    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    /**
     * @notice Updates the sell fee.
     * @param newSellFee The new sell fee value to be set.
     * @dev The sell fee must not exceed 5%.
     */
    function updateSellFee(uint256 newSellFee) external onlyOwner {
        require(newSellFee <= 5, "FeeManager: sell fee cannot exceed 5%");
        sellFee = newSellFee;
        emit SellFeeUpdated(newSellFee);
    }
}
