pragma solidity ^0.8.0;

contract RefactoredContract {
    // Mapping to store balances
    mapping(address => uint64) private balances;

    // Custom error for invalid address
    error InvalidAddress();

    /**
     * @dev Returns the balance of the given address.
     * @param account The address to query the balance of.
     * @return The balance of the given address.
     */
    function balanceOf(address account) public view returns (uint64) {
        // Validate the input address
        if (account == address(0)) {
            revert InvalidAddress();
        }

        // Return the balance from the mapping
        return balances[account];
    }
}
