pragma solidity ^0.8.0;

contract MintingContract {
    uint256 public guaranteedWhitelistNumMinted;
    uint256 public competitiveWhitelistNumMinted;

    /**
     * @dev Returns the total number of tokens minted.
     * This function ensures that the guaranteed whitelist minted count
     * does not exceed the competitive whitelist minted count.
     */
    function getTotalMinted() public view returns (uint256) {
        require(
            guaranteedWhitelistNumMinted <= competitiveWhitelistNumMinted,
            "Guaranteed whitelist minted count exceeds competitive whitelist minted count"
        );

        return guaranteedWhitelistNumMinted + competitiveWhitelistNumMinted;
    }
}
