// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract DevshipTransfer {
    address private developer;

    event DeveloperTransferred(address indexed previousDeveloper, address indexed newDeveloper);

    constructor(address initialDeveloper) {
        require(initialDeveloper != address(0), "Initial developer address cannot be zero");
        developer = initialDeveloper;
        emit DeveloperTransferred(address(0), initialDeveloper);
    }

    modifier onlyDeveloper() {
        require(msg.sender == developer, "Caller is not the developer");
        _;
    }

    function transferDeveloperRole(address newDeveloper) external onlyDeveloper {
        require(newDeveloper != address(0), "New developer address cannot be zero");
        emit DeveloperTransferred(developer, newDeveloper);
        developer = newDeveloper;
    }

    function getDeveloper() external view returns (address) {
        return developer;
    }
}
```

