// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Token {
    // Mapping to store allowances
    mapping(address => mapping(address => uint256)) private allowances;

    // Event to log approvals
    event Approval(address indexed owner, address indexed spender, uint256 value);

    /**
     * @dev Approves `spender` to spend `amount` on behalf of the caller.
     * @param spender The address which will be allowed to spend the tokens.
     * @param amount The amount of tokens to approve.
     * @return success Returns true if the operation is successful.
     */
    function approve(address spender, uint256 amount) public returns (bool success) {
        require(spender != address(0), "Invalid spender address");

        // Update the allowance mapping
        allowances[msg.sender][spender] = amount;

        // Emit the Approval event
        emit Approval(msg.sender, spender, amount);

        return true;
    }

    /**
     * @dev Returns the remaining number of tokens that `spender` is allowed to spend on behalf of `owner`.
     * @param owner The address of the token owner.
     * @param spender The address of the spender.
     * @return remaining The remaining allowance.
     */
    function allowance(address owner, address spender) public view returns (uint256 remaining) {
        return allowances[owner][spender];
    }
}

